// Utility functions for data manipulation
export const getProjectById = (id) => {
  return portfolioData.find(project => project.id === id);
};

export const getProjectsByCategory = (category) => {
  return portfolioData.filter(project => project.category === category);
};

export const getProjectsByYear = (year) => {
  return portfolioData.filter(project => project.year === year);
};

export const getCategories = () => {
  const categories = [...new Set(portfolioData.map(project => project.category))];
  return categories.sort();
};

export const getRandomProjects = (count = 3, excludeId = null) => {
  const filtered = excludeId ? 
    portfolioData.filter(project => project.id !== excludeId) : 
    portfolioData;
  const shuffled = [...filtered].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
};

export const getTotalProjectCount = () => {
  return portfolioData.length;
};
